#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <stdbool.h>

#define TRUE    1
#define FALSE   0

typedef enum brace_type_ {
    BRACE_TYPE_NONE     = 0,
    BRACE_TYPE_PAREN    = 1, // ( )
    BRACE_TYPE_SQARE    = 2, // [ ]
    BRACE_TYPE_BRACE    = 3, // { }
    BRACE_TYPE_MAX      = BRACE_TYPE_BRACE + 1
} brace_type_t;

char my_stack[10000] = {0};
int my_tos = -1;

int my_push(brace_type_t type)
{
    my_stack[++my_tos] = type;
    return(0);
}

int my_pop(brace_type_t *type_p)
{
    *type_p = my_stack[my_tos];
    if (my_tos >= 0) {
        my_stack[my_tos--] = BRACE_TYPE_NONE;
    } else {
        printf("\nStack Underflow\n");
    }

    return(0);
}

bool isValid(char* s) {
    char bracket_cnt[255] = {0};
    unsigned int len = 0;
    int i = 0;
    brace_type_t type = BRACE_TYPE_NONE;

    len = strlen(s);

    for (i = 0; i < len; i ++) {
        if (s[i] == '(') {
            printf("\nFound (\n");
            my_push(BRACE_TYPE_PAREN);
            bracket_cnt['(']++;
            printf("\n( cnt = %d, type = %d\n", bracket_cnt['('], BRACE_TYPE_PAREN);
        } else if (s[i] == '[') {
            printf("\nFound [\n");
            my_push(BRACE_TYPE_SQARE);
            bracket_cnt['[']++;
            printf("\n[ cnt = %d, type = %d\n", bracket_cnt['['], BRACE_TYPE_SQARE);
        } else if (s[i] == '{') {
            printf("\nFound {\n");
            my_push(BRACE_TYPE_BRACE);
            bracket_cnt['{']++;
            printf("\n{ cnt = %d, type = %d\n", bracket_cnt['{'], BRACE_TYPE_BRACE);
        } else if (s[i] == ')') {
            printf("\nFound )\n");
            my_pop(&type);
            printf("\n( cnt = %d, type = %d\n", bracket_cnt['('], type);
            if ((type != BRACE_TYPE_PAREN) || (bracket_cnt['('] == 0) ) {
                return(FALSE);
            } else {
                bracket_cnt['(']--;
            }
        }  else if (s[i] == ']') {
            printf("\nFound ]\n");
            my_pop(&type);
            printf("\n[ cnt = %d, type = %d\n", bracket_cnt['['], type);
            if ((type != BRACE_TYPE_SQARE) || (bracket_cnt['['] == 0) ) {
                return(FALSE);
            } else {
                bracket_cnt['[']--;
            }
        }  else if (s[i] == '}') {
            printf("\nFound }\n");
            my_pop(&type);
            printf("\n{ cnt = %d, type = %d\n", bracket_cnt['{'], type);
            if ((type != BRACE_TYPE_BRACE) || (bracket_cnt['{'] == 0) ) {
                return(FALSE);
            } else {
                bracket_cnt['{']--;
            }
        }
    }

    return(TRUE);
}

